DROP TABLE IF EXISTS `product`;
CREATE TABLE `product` (
                           `id` varchar(32) NOT NULL,
                           `name` varchar(64) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci DEFAULT NULL,
                           `price` double(32,8) DEFAULT NULL,
  `purchase_num` int DEFAULT NULL,
  `product_intro` varchar(256) DEFAULT NULL,
  `product_imgs` varchar(1024) DEFAULT NULL,
  `gmt_created` datetime DEFAULT NULL,
  `gmt_modified` datetime DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb3;

